import React from "react";
import { useState } from "react";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import axios from "axios";
import { Button,Box ,styled } from "@mui/material";

import { useNavigate } from "react-router-dom";



export default function Add() {

  const navigate = useNavigate();
  const [input, setInput] = useState({
    title: "",
    subTitle: "",
    price: "",
    mrpPrice: "",
  });

  
  
  
  const productRequest = () => {
    const res = axios
      .post(`http://localhost:5000/api/add`, {
        title: input.title,
        subTitle: input.subTitle,
        price: input.price,
        mrpPrice: input.mrpPrice,
      })
      .catch((err) => console.log(err));

    const data = res.data;
    console.log(data);
    return data;
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(input);
    productRequest()
      .then((data) => console.log(data))
      .then(() => navigate("/SignIn"));
  };

  const handleChange = (e) => {
    console.log("hiii");
    setInput((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
    // console.log(value);
  };

  return (
    <Box sx={{margin:"50px auto " , width:"50%", }} >
      <form onSubmit={handleSubmit} width="50%" sx={{backgroundColor:"red"}}>
    <Typography variant="h6" gutterBottom sx={{textAlign:"center"}}>
      Add New Project
    </Typography>
    <Box sx={{ padding: "20px" }}>
      <Grid container spacing={2}>
        <Grid item xs={12} md={6}>
          <TextField
            type="text"
            label="Product Name Title"
            onChange={handleChange}
            name="title"
            fullWidth
            
            variant="standard"
            value={input.title}
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            required
            onChange={handleChange}
            name="subTitle"
            value={input.subTitle}
            id="subTitle"
            label="Sub Title"
            fullWidth
            autoComplete="cc-number"
            variant="standard"
            type="text"
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            required
            onChange={handleChange}
            name="price"
            value={input.price}
            id="price"
            label="Sale Price"
            fullWidth
            autoComplete="cc-number"
            variant="standard"
            type="number"
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            required
            onChange={handleChange}
            name="mrpPrice"
            value={input.mrpPrice}
            id="mrpPrice"
            label="MRP Price"
            fullWidth
            variant="standard"
            type="number"
          />
        </Grid>
        <Grid item  xs={12} md={12} mar>   <Button  variant="contained" color="success" type="submit" fullWidth>Add</Button></Grid>
      </Grid>
    </Box>
  </form>
    </Box>
  );
}
