import { Box, Button, Grid, TextField, Typography } from "@mui/material";
import React from "react";
import { useState } from "react";
// import axios from "axios";

const Add = () => {
  const [input, setInput] = useState({
    title: null,
    subTitle: null,
    price: "",
    mrpPrice: "",
  });

  // const productRequest = async () => {

  //   const res = await axios

  //     .post("http://localhost:5000/", {

  //       title: inputs.title,

  //       description: inputs.description,

  //       image: inputs.imageURL,

  //       user: localStorage.getItem("userId"),

  //     })

  //     .catch((err) => console.log(err));

  //   const data = await res.data;

  //   return data;

  // };
  return (
    <div>
      <form>
        <Typography
          sx={{ marginTop: "50px" }}
          textAlign="center"
          variant="h4"
          width="100%"
        >
          Add A New Product
        </Typography>

        <Box margin="30px">
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <TextField
                required
                id="cardName"
                label="Name on card"
                fullWidth
                autoComplete="cc-name"
                variant="standard"
                value={input}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                required
                id="cardNumber"
                label="Card number"
                fullWidth
                autoComplete="cc-number"
                variant="standard"
                value={input}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                required
                id="expDate"
                label="Expiry date"
                fullWidth
                autoComplete="cc-exp"
                variant="standard"
                value={input}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                required
                id="cvv"
                label="CVV"
                helperText="Last three digits on signature strip"
                fullWidth
                autoComplete="cc-csc"
                variant="standard"
                
              />
            </Grid>
          </Grid>
        </Box>
      </form>
    </div>
  );
};

export default Add;
