import { Button, ButtonGroup, TableCell, TableRow } from "@mui/material";
 import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import RemoveRedEyeIcon from '@mui/icons-material/RemoveRedEye';

const Book = (props) => {
     const history = useNavigate();
  const { _id, title, subTitle, price, mrpPrice } = props.products;
    
  const deleteHandler = async () => {
      await axios
        .delete(`http://localhost:8080/add-product/${_id}`)
        .then((res) => res.data)
        .then(() => history("/"))
        .then(() => history("/"));
    };
 

  const viewHandler = async () =>{

  }

  return (
    <div>
       
      <TableCell align="right" >
        {title}
      </TableCell>
      <TableCell align="right">{subTitle}</TableCell>
      <TableCell align="right">{price}</TableCell>
      <TableCell align="right">{mrpPrice}</TableCell>
      <TableCell>
      <ButtonGroup variant="contained" aria-label="outlined primary button group">
  <Button>One</Button>
  <Button>Two</Button>
  <Button>Three</Button>
</ButtonGroup>
      </TableCell>
      <TableCell align="right">
      
        <Button color="error"  variant="contained" sx={{ mt: "auto" }}>
         <RemoveRedEyeIcon />
        </Button>
      </TableCell>
      <TableCell align="right">
        <Button
          LinkComponent={Link}
          to={`/add-product/${_id}`}
          color="warning"
          variant="contained"
          sx={{ mt: "auto" }}
        >
          Update
        </Button>
      </TableCell>
      <TableCell align="right">
      
        <Button color="error" onClick={deleteHandler} variant="contained" sx={{ mt: "auto" }}>
          Delete
        </Button>
      </TableCell>
    </div>
  );
};

export default Book;
