import { Button, ButtonGroup, TableCell, TableRow } from "@mui/material";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import RemoveRedEyeIcon from "@mui/icons-material/RemoveRedEye";
import ModeEditOutlineIcon from "@mui/icons-material/ModeEditOutline";
import DeleteIcon from "@mui/icons-material/Delete";

const Book = (props) => {
  const history = useNavigate();
  const { _id, title, subTitle, price, mrpPrice } = props.products;

  const deleteHandler = async () => {
    await axios
      .delete(`http://localhost:8080/product/${_id}`)
      .then((res) => res.data)
      .then(() => history("/"))
      .then(() => history("/"));
  };

  // const viewHandler = async () => {};

  return (
    <div>
      <TableCell align="right">{title}</TableCell>
      <TableCell align="right">{subTitle}</TableCell>
      <TableCell align="right">{price}</TableCell>
      <TableCell align="right">{mrpPrice}</TableCell>
      <TableCell>
        <ButtonGroup 
          variant="contained"
          aria-label="outlined primary button group"
        >
          <Button sx={{borderRadius : 0}} color="info">
            <RemoveRedEyeIcon />
          </Button>
          <Button
            LinkComponent={Link}
            to={`/product/${_id}`}
            sx={{borderRadius : 0}}
            color="warning"
          >
            <ModeEditOutlineIcon />
          </Button>
          <Button sx={{borderRadius : 0}} color="error" onClick={deleteHandler}>
            <DeleteIcon />
          </Button>
        </ButtonGroup>
      </TableCell>
    </div>
  );
};

export default Book;
