import { Button, TableCell, TableRow } from "@mui/material";
 import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Book = (props) => {
     const history = useNavigate();
  const { _id, title, subTitle, price, mrpPrice } = props.products;
    const deleteHandler = async () => {
      await axios
        .delete(`http://localhost:8080/add-product/${_id}`)
        .then((res) => res.data)
        .then(() => history("/"))
        .then(() => history("/"));
    };
  console.log(props.products);

  return (
    <div>
       
      <TableCell align="right" >
        {title}
      </TableCell>
      <TableCell align="right">{subTitle}</TableCell>
      <TableCell align="right">{price}</TableCell>
      <TableCell align="right">{mrpPrice}</TableCell>
      <TableCell align="right">
      
        <Button color="error" onClick={viewHandler} variant="contained" sx={{ mt: "auto" }}>
          Details
        </Button>
      </TableCell>
      <TableCell align="right">
        <Button
          LinkComponent={Link}
          to={`/product/${_id}`}
          color="warning"
          variant="contained"
          sx={{ mt: "auto" }}
        >
          Update
        </Button>
      </TableCell>
      <TableCell align="right">
      
        <Button color="error" onClick={deleteHandler} variant="contained" sx={{ mt: "auto" }}>
          Delete
        </Button>
      </TableCell>
    </div>
  );
};

export default Book;
