import React, { useEffect, useState } from "react";
import MaterialTable from "@mui/material"
import axios from "axios";
import {
  Button,
  Container,
  Grid,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import { Link } from "react-router-dom";
import Product from "./products/product";

const URL ="http://localhost:8080/add-product";
const fetchHandler = async() =>{
    return await axios.get(URL).then((res)=>res.data
    );

} 




const Home = () => {
  const [products, setProducts] = useState();
  useEffect( ( ) =>{
    fetchHandler().then((data)=> setProducts(data.product))
  },[] )
  return (

    <div>
      <Container>
        <Grid container padding="20px 0 20px 0" textAlign="left">
          <Grid item xs={12} md={10}>
            <Typography variant="h4" marginBottom={1} component="h3">
              View All Data
            </Typography>
          </Grid>
          <Grid item xs={12} md={2} textAlign="right">
            <Button
              style={{ width: "100%" }}
              variant="contained"
              color="success"
              component={Link}
              to="./addProduct"
            >
              <AddIcon /> Add Data
            </Button>
          </Grid>
        </Grid>

        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Product Name</TableCell>
                <TableCell align="right">Sub Title</TableCell>
                <TableCell align="right">Sale Price</TableCell>
                <TableCell align="right">MRP Price</TableCell>
                <TableCell align="right">View</TableCell>
                <TableCell align="right">Update</TableCell>
                <TableCell align="right">Delete</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
            {products &&
          products.map((products, i) => (
            <TableRow key={i}>
               <TableCell  component="td" scope="row">
        {i+1}
      </TableCell>
              <Product products={products} />
            </TableRow>
          ))}

            </TableBody>
          </Table>
        </TableContainer>
      </Container>

      <MaterialTable
      title="Multiple Actions Preview"
      columns={[
        { title: 'Name', field: 'name' },
        { title: 'Surname', field: 'surname' },
        { title: 'Birth Year', field: 'birthYear', type: 'numeric' },
        {
          title: 'Birth Place',
          field: 'birthCity',
          lookup: { 34: 'İstanbul', 63: 'Şanlıurfa' },
        },
      ]}
      data={[
        { name: 'Mehmet', surname: 'Baran', birthYear: 1987, birthCity: 63 },
        { name: 'Zerya Betül', surname: 'Baran', birthYear: 2017, birthCity: 34 },
      ]}        
      actions={[
        {
          icon: 'save',
          tooltip: 'Save User',
          onClick: (event, rowData) => alert("You saved " + rowData.name)
        },
        {
          icon: 'delete',
          tooltip: 'Delete User',
          onClick: (event, rowData) => confirm("You want to delete " + rowData.name)
        }
      ]}
    />
    </div>
  );
};
export default Home;
