import { Box, Button, Grid, TextField, Typography } from "@mui/material";
import React from "react";
import { useState } from "react";
// import axios from "axios";

const Add = () => {
  const [input, setInput] = useState({
    title: null,
    subTitle: null,
    price: "",
    mrpPrice: "",
  });

  // const productRequest = async () => {

  //   const res = await axios

  //     .post("http://localhost:5000/", {

  //       title: inputs.title,

  //       description: inputs.description,

  //       image: inputs.imageURL,

  //       user: localStorage.getItem("userId"),

  //     })

  //     .catch((err) => console.log(err));

  //   const data = await res.data;

  //   return data;

  // };
  return (
    <div>
      <form>
        <Typography
          sx={{ marginTop: "50px" }}
          textAlign="center"
          variant="h4"
          width="100%"
        >
          Add A New Product
        </Typography>

        <Box margin="30px">
          <Grid
            container
            spacing={{ xs: 3, md: 3 }}
            width="100%"
            justifyContent="center"
          >
            <Grid item spacing={2} lg={6} md={12} xs={12}>
              <TextField

                size="small"
                name="title"
                id="title"
                required
                fullWidth
                label="Product Title Name"
                value={input}
               
              />
            </Grid>
            <Grid spacing={2} item lg={6} md={12} xs={12}>
              <TextField
                size="small"
                name="subTitle"
                id="subTitle"
                required
                fullWidth
                label="Product Sub Title"
                value={input}
              
              />
            </Grid>
            <Grid spacing={2} item lg={6} md={12} xs={12}>
              <TextField
                size="small"
                required
                fullWidth
                name="mrpPrice"
                label="MRP Price"
                type="number"
                id="mrp_price"
                autoComplete="new-mrp_price"
                value={input}
              />
            </Grid>
            <Grid spacing={2} item lg={6} md={12} xs={12}>
              <TextField
                size="small"
                required
                fullWidth
                name="price"
                label="Sale Price"
                type="number"
                id="sale_price"
                autoComplete="new-sale_price"
                value={input}
              />
            </Grid>
            <Grid item marginTop="20px">
              <Button variant="contained" color="success" spacing={2} item>
                Add
              </Button>
            </Grid>
          </Grid>
        </Box>
      </form>
    </div>
  );
};

export default Add;
