import React, { useState } from 'react';
import '../assets/css/signUp.css';

const SignUp = () => {
  const [input, setInput] = useState({
    userName: "",
    email:"",
    password:"",
    password2:""
  });
 
    // const onTextFieldChange =(e) => {

  //   const { name, value } = e.target;

  //   setStudent({ ...student, [name]:value});

  //   console.log(name, value);

  // }
  const checkValidation = (e) =>{
    setInput((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
     
    }));
    console.log(input)
  }

  const handleSubmit = () =>{
   
      console.log(input)
  }
  return (
    <div className="login-box">
    <div className="left">
      <h1>Sign up</h1>
      
     <form  onSubmit={handleSubmit}>
     <input className='input' type="text" name="userName"  placeholder="Username" autoComplete="off" value={input.userName} onChange={checkValidation}/>
      <input className='input' type="email" name="email" placeholder="E-mail" value={input.email} onChange={checkValidation}/>
      <input className='input' type="password" name="password" placeholder="Password" autoComplete="off" value={input.password} onChange={checkValidation}/>
      <input className='input' type="password" name="password2" placeholder="Retype password" value={input.password2} onChange={checkValidation}/>
      
      <input className='input' type="submit" name="signup_submit" value="Sign me up" />
     </form>
    </div>
    
    <div className="right">
      <span className="loginwith">Sign in with<br />social network</span>
      
      <button className="social-signin facebook">Log in with facebook</button>
      <button className="social-signin twitter">Log in with Twitter</button>
      <button className="social-signin google">Log in with Google+</button>
    </div>
    <div className="or">OR</div>
  </div>
  )
}

export default SignUp
